import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App = () => {
  const [tasks, setTasks] = useState([]);  // State for tasks
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    dueDate: '',
    status: 'pending',
  });
  const [aiInsights, setAiInsights] = useState('');  // State to store AI insights
  const [loading, setLoading] = useState(false);  // Loading state for AI insights


  // Fetch tasks from backend when the component mounts
  useEffect(() => {
    axios
      .get('http://localhost:5000/tasks')  // Fetch tasks from backend
      .then((response) => {
        setTasks(response.data);  // Update state with fetched tasks
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);  // Log any error
      });
  }, []);  // Empty array ensures this runs once after the component mounts

  // Handle form submission to add a new task
  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent default form submission
    axios
      .post('http://localhost:5000/tasks', newTask)  // Send new task data to backend
      .then((response) => {
        setTasks([...tasks, response.data]);  // Add new task to task list
        setNewTask({ title: '', description: '', dueDate: '', status: 'pending' });  // Clear form
      })
      .catch((error) => {
        console.error('Error creating task:', error);  // Log any error
      });
  };

  // Handle task update
  const handleUpdateTask = (id, updatedData) => {
    axios
      .put(`http://localhost:5000/tasks/${id}`, updatedData)
      .then((response) => {
        // Update the task in state with the updated task data
        setTasks(tasks.map((task) => (task.id === id ? response.data : task)));
      })
      .catch((error) => {
        console.error('Error updating task:', error);
      });
  };

  // Handle task delete
  const handleDeleteTask = (id) => {
    axios
      .delete(`http://localhost:5000/tasks/${id}`)
      .then(() => {
        // Remove the deleted task from the state
        setTasks(tasks.filter((task) => task.id !== id));
      })
      .catch((error) => {
        console.error('Error deleting task:', error);
      });
  };

  // Filter tasks by status
  const filterTasksByStatus = (status) => {
    return tasks.filter((task) => task.status === status);
  };

  // Filter tasks by due date
  const filterTasksByDueDate = (dueDate) => {
    return tasks.filter(
      (task) => new Date(task.dueDate).toLocaleDateString() === new Date(dueDate).toLocaleDateString()
    );
  };

  // Request AI-powered insights based on current tasks
  const getAIInsights = async () => {
    setLoading(true);  // Set loading state to true while fetching insights
    try {
      const response = await axios.post('http://localhost:5000/ai-insights', {
        taskList: tasks,  // Send the current task list to the backend
      });
      setAiInsights(response.data.insights);  // Store the AI insights in state
    } catch (error) {
      console.error('Error fetching AI insights:', error);
      setAiInsights('Failed to fetch AI insights. Please try again later.');
    } finally {
      setLoading(false);  // Reset loading state after fetching is complete
    }
  };

  // index.js (or App.js depending on your setup)

if ('serviceWorker' in navigator) {
  // Register service worker
  navigator.serviceWorker.register('/service-worker.js')
    .then((registration) => {
      console.log('Service Worker registered with scope:', registration.scope);
    })
    .catch((error) => {
      console.error('Service Worker registration failed:', error);
    });
}

  return (
    <div>
      <h1>Task Dashboard</h1>

      {/* Task creation form */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Task Title"
          value={newTask.title}
          onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
        />
        <textarea
          placeholder="Task Description"
          value={newTask.description}
          onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
        />
        <input
          type="date"
          value={newTask.dueDate}
          onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })}
        />
        <button type="submit">Add Task</button>
      </form>

      {/* Display AI insights */}
      <div>
        <h2>AI Insights</h2>
        <button onClick={getAIInsights} disabled={loading}>
          {loading ? 'Loading AI Insights...' : 'Get AI Insights'}
        </button>
        <div>
          {aiInsights && (
            <p>{aiInsights}</p>  // Display the AI insights
          )}
        </div>
      </div>

      {/* Display Task List */}
      <ul>
        {tasks.map((task) => (
          <li key={task.id} style={{ textDecoration: task.status === 'completed' ? 'line-through' : 'none' }}>
            <strong>{task.title}</strong> - {task.status} (Due: {task.dueDate})
            <button onClick={() => handleUpdateTask(task.id, { status: 'completed' })}>
              Mark as Completed
            </button>
            <button onClick={() => handleDeleteTask(task.id)}>
              Delete Task
            </button>
          </li>
        ))}
      </ul>

      {/* Filtered tasks */}
      <div>
        <h2>Due Today</h2>
        <ul>
          {filterTasksByDueDate(new Date()).map((task) => (
            <li key={task.id}>{task.title} - {task.status}</li>
          ))}
        </ul>

        <h2>Completed Tasks</h2>
        <ul>
          {filterTasksByStatus('completed').map((task) => (
            <li key={task.id}>{task.title} - {task.dueDate}</li>
          ))}
        </ul>

        <h2>Overdue Tasks</h2>
        <ul>
          {filterTasksByDueDate(new Date()).map((task) => (
            <li key={task.id}>{task.title} - {task.dueDate}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
